//we pass state as first argument, then action object
//we need default returns something back 
//return.action.payload- this is to check the action is working and if reducer can get updated

export default function userReducer(state = {users: []}, action ) {

    switch(action.type){
        case 'FETCH_USER':
            return{user: action.payload}    // new redux state, 
        case 'ADD_ACCOUNT':                        
            return {...state, users: [...state.users, action.payload]}      //keep all the accounts that are in state and add the new one
        default:
            return state  
    }
}
